mapper.select:
SELECT booking.booking_id, booking.booking_state, booking.booknow, booking.doctor_id, booking.user_id, booking.book_name, booking.book_idCard, booking.book_num, booking.book_address FROM booking
booking.book_idCard=#{bookIdCard}
and booking.book_name=#{bookName}
and booking_state=#{bookingState}
and booking.booking_id=#{bookingId}

mapper.insert:
insert into booking (
booking_state, booknow, doctor_id, user_id, book_name, book_idCard, book_num, book_address,
)values(
#{bookingState}, #{booknow}, #{doctorId}, #{userId}, #{bookName}, #{bookIdCard}, #{bookNum}, #{bookAddress},
)

mapper.update:
update booking set
booking_state=#{bookingState}, booknow=#{booknow}, doctorId=#{doctorId}, user_id=#{userId}, book_name=#{bookName}, book_idCard=#{bookIdCard}, book_num=#{bookNum}, book_address=#{bookAddress},
booking_Id=#{bookingId}
